
module.exports = Room;

let nextRoomId = 1;

function Room(name, namespace, isPublic =true, id =-1) {
  let self = this;

  self.id         = (id === -1 ? nextRoomId++ : id);
  self.name       = name;
  self.namespace  = namespace;
  self.isPublic   = isPublic;

  self.items      = [];

  self.addItem = function(item) {
    self.items.push(item);
  }

  self.clear = function() {
    self.items = [];
  };
}
